import { HtmlNode, HtmlNodeModel } from '@logicflow/core'
import  parallelNode from './parallel.vue' 
import { createApp } from 'vue';
/**
 * 并行网关
 */
export class ParallelModel extends HtmlNodeModel {
    createId() {
      return Math.random() + '_parallel'
    }
    setAttributes() {
      const width = 200
      const height = 130
      this.width = width
      this.height = height
      this.textHeight = 60
      this.properties={
          icon:"data:image/png;base64,"
      }
      this.text = {
        ...this.text,
        y: this.y - this.height / 2
      }
  
      this.anchorsOffset = [
        {
          x: width / 2,
          y: 0,
          isSourceAnchor: false,
          isTargetAnchor: true
        }
      ]
    }
  }
  export class ParallelNode extends HtmlNode {
    setHtml(rootEl: SVGForeignObjectElement) {
      rootEl.innerHTML = '' 
    }
  }
  
  export default {
    type: 'parallel',
    view: ParallelNode,
    model: ParallelModel
  }